import React, { useState } from "react";

const Pagination = ({ postsPerPage, totalPosts, paginate }) => {
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
    pageNumbers.push(i);
  }

  cosnt[(isClick, setIsClick)] = useState();

  return (
    <div className="sticky bottom-10 flex justify-center">
      <nav className="flex justify-center w-80 h-14 bg-white rounded-2xl shadow-lg">
        <ul className="flex items-center w-full justify-evenly">
          {pageNumbers.map((number) => (
            <li key={number} className="page-item">
              <span
                className="focus-ring-2"
                onClick={() => paginate(number)}
                className="page-link"
              >
                {number}
              </span>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
};

export default Pagination;
