import React from "react";
import profile from "../../images/profile.svg";

const Comment = ({ comment }) => {
  return (
    <div className="p-4">
      <div className="flex">
        <img className="w-6 h-6" src={profile} alt="" />
        <div className="pl-3">
          <h3 className="text-sm font-bold">{`유저 ${comment.userId}`}</h3>
          <span className="text-gray-light">00-00-00</span>
        </div>
      </div>
    </div>
  );
};

export default Comment;
